sp_widget.client_script:
api.controller = function($scope, $rootScope, spUtil, $timeout) {
    var c = this;

    var CONFIG = {
        baseUrl: 'https://dev357601.service-now.com/sp?id=',
        detailsPage: 'cmdb_details',
        iframeId: 'serversIframe',
        ajaxClass: 'global.PortalFilterPrefs'
    };

    c.networksLoading = false;
    c.searchTerm = '';

    c.refreshNetworks = function() {
        c.networksLoading = true;

        c.server.get({ action: 'refreshNetworks' })
            .then(function(response) {
                if (response.data) {
                    c.data.networkTypes = response.data.networkTypes;
                    c.data.totalNetworks = response.data.totalNetworks;
                    c.data.totalTypes = response.data.totalTypes;
                }
            })
            .catch(function(error) {
                gs.error('Error refreshing networks:', error);
            })
            .finally(function() {
                c.networksLoading = false;
                spUtil.update($scope);
            });
    };

 c.goToDetails = function(sysClassName) {
    console.log('Clicou em network, source deve ser: network'); // LOG 7

    var ga = new GlideAjax('global.PortalFilterPrefs');
    ga.addParam('sysparm_name', 'saveFilter');
    ga.addParam('sysparm_filter', sysClassName);
    ga.addParam('sysparm_source', 'network'); // VERIFIQUE AQUI

    console.log('Enviando source: network'); // LOG 8

    ga.getXMLAnswer(function(response) {
        console.log('Resposta:', response);
        if (response === 'ok') {
            window.location.href = 'https://dev357601.service-now.com/sp?id=cmdb_details&filter=' + 
                                   encodeURIComponent(sysClassName);
        }
    });
};
    c.$onInit = function() {
        c.refreshNetworks();
    };
};

