sp_widget.script:
var NETWORK_CLASS_MAPPING = {
    'cmdb_ci_netgear': { name: 'Network Gear', image: 'networkgear.png' },
    'cmdb_ci_router': { name: 'Router', image: 'router.png' },
    'cmdb_ci_switch': { name: 'Switch', image: 'switch.png' },
    'cmdb_ci_firewall': { name: 'Firewall', image: 'firewall.png' },
    'cmdb_ci_load_balancer': { name: 'Load Balancer', image: 'loadbalancer.png' },
    'cmdb_ci_wireless_ap': { name: 'Wireless AP', image: 'wireless.png' },
    'cmdb_ci_network': { name: 'Generic Network', image: 'genericnetwork.png' }
};

var CONFIG = {
    defaultImage: 'genericnetwork.png',
    operationalStatus: '1',
    table: 'cmdb_ci_netgear'
};

function getNetworkTypes() {
    var networkTypes = [];
    var totalNetworks = 0;

    var aggregate = new GlideAggregate(CONFIG.table);
    aggregate.addAggregate('COUNT');
    aggregate.addQuery('operational_status', CONFIG.operationalStatus);
    aggregate.groupBy('sys_class_name');
    aggregate.query();

    while (aggregate.next()) {
        var sysClassName = aggregate.sys_class_name.toString();
        var count = parseInt(aggregate.getAggregate('COUNT'), 10);
        var info = NETWORK_CLASS_MAPPING[sysClassName] || {
            name: sysClassName,
            image: CONFIG.defaultImage
        };

        networkTypes.push({
            name: info.name,
            image: info.image,
            count: count,
            sys_class_name: sysClassName
        });

        totalNetworks += count;
    }

    networkTypes.sort(function(a, b) {
        return b.count - a.count;
    });

    return {
        networkTypes: networkTypes,
        totalNetworks: totalNetworks,
        totalTypes: networkTypes.length
    };
}

try {
    if (!input || input.action === 'refreshNetworks') {
        var result = getNetworkTypes();
        data.networkTypes = result.networkTypes;
        data.totalNetworks = result.totalNetworks;
        data.totalTypes = result.totalTypes;
    }
} catch (error) {
    gs.error('Erro ao obter dispositivos de rede: ' + error.message);
    data.networkTypes = [];
    data.totalNetworks = 0;
    data.totalTypes = 0;
}
