sp_widget.client_script:
api.controller = function($scope, $rootScope, spUtil, $sce) {
    var c = this;

    var VIEW_CONFIG = {
        servers: {
            title: 'Servers Management',
            icon: 'fa-server',
            url: 'https://dev357601.service-now.com/sp?id=cmdb_servers_page'
        },
        network: {
            title: 'Network Devices', 
            icon: 'fa-sitemap',
            url: 'https://dev357601.service-now.com/sp?id=cmdb_network'
        },
        server_details: {
            title: 'Server Details',
            icon: 'fa-list'
        }
    };

    c.currentView = 'servers';
    c.filterParam = '';

    function getViewConfig(viewName) {
        return VIEW_CONFIG[viewName] || {};
    }

    c.getViewIcon = function() {
        return getViewConfig(c.currentView).icon || 'fa-file';
    };

    c.getViewTitle = function() {
        return getViewConfig(c.currentView).title || c.currentView;
    };

    c.getServersUrl = function() {
        return $sce.trustAsResourceUrl(VIEW_CONFIG.servers.url);
    };

    c.getNetworkUrl = function() {
        return $sce.trustAsResourceUrl(VIEW_CONFIG.network.url);
    };

    c.changeView = function(viewName, filterParam) {
        if (!VIEW_CONFIG[viewName]) return;

        c.currentView = viewName;
        c.filterParam = filterParam || '';
        spUtil.update($scope);
    };

    $rootScope.$on('cmdb.view.changed', function(event, data) {
        var viewName = typeof data === 'object' ? data.view : data;
        var filter = typeof data === 'object' ? data.filter : '';
        c.changeView(viewName, filter);
    });

    c.$onInit = function() {
        c.changeView('servers');
    };
};

