sp_widget.script:
var currentTime = new GlideDateTime().getDisplayValue();

if (input && input.action === 'refreshServers') {
    data.refreshTime = currentTime;
} else {
    data.initialized = true;
    data.initializedTime = currentTime;
}

// Dados do CMDB
data.viewConfig = {
    servers: {
        table: 'cmdb_ci_server',
        count: new GlideAggregate('cmdb_ci_server').getCount()
    },
    network: {
        table: 'cmdb_ci_netgear',
        count: new GlideAggregate('cmdb_ci_netgear').getCount()
    }
};
