sp_widget.client_script:
api.controller = function($scope, $rootScope, spUtil, $timeout) {
    var c = this;

    var CONFIG = {
        detailsUrl: 'https://dev357601.service-now.com/sp?id=cmdb_details',
        ajaxClass: 'global.PortalFilterPrefs'
    };

    c.serversLoading = false;
    c.searchTerm = '';

    c.refreshServers = function() {
        c.serversLoading = true;

        c.server.get({ action: 'refreshServers' })
            .then(function(response) {
                if (response.data) {
                    c.data.serverTypes = response.data.serverTypes;
                    c.data.totalServers = response.data.totalServers;
                    c.data.totalTypes = response.data.totalTypes;
                }
            })
            .catch(function(error) {
                gs.error('Error refreshing servers:', error);
            })
            .finally(function() {
                c.serversLoading = false;
                spUtil.update($scope);
            });
    };

   c.goToDetails = function(sysClassName) {
    var ga = new GlideAjax('global.PortalFilterPrefs');
    ga.addParam('sysparm_name', 'saveFilter');
    ga.addParam('sysparm_filter', sysClassName);
    ga.addParam('sysparm_source', 'servers'); // ADICIONE ESTA LINHA

    ga.getXMLAnswer(function(response) {
        if (response === 'ok') {
            window.location.href = 'https://dev357601.service-now.com/sp?id=cmdb_details&filter=' + 
                                   encodeURIComponent(sysClassName);
        }
    });
};

    c.$onInit = function() {
        c.refreshServers();
    };
};

