sp_widget.script:
var SERVER_CLASS_MAPPING = {
    'cmdb_ci_aix_server': { name: 'AIX Server', image: 'aixserver.png', construcao: true },
    'cmdb_ci_esx_server': { name: 'ESX Server', image: 'aixserver.png' },
    'cmdb_ci_hcx_server': { name: 'HCX Server', image: 'aixserver.png' },
    'cmdb_ci_hpux_server': { name: 'HPUX Server', image: 'aixserver.png', construcao: true },
    'cmdb_ci_hyperv_server': { name: 'Hyper-V Servers', image: 'aixserver.png' },
    'cmdb_ci_linux_server': { name: 'Linux Server', image: 'linuxserver.png' },
    'cmdb_ci_server': { name: 'Generic Server', image: 'genericservers.png' },
    'cmdb_ci_solaris_server': { name: 'Solaris Server', image: 'aixserver.png', construcao: true },
    'cmdb_ci_vmware_vcenter': { name: 'VMware Vcenter', image: 'aixserver.png' },
    'cmdb_ci_win_server': { name: 'Windows Server', image: 'windowsserver.png' }
};

var CONFIG = {
    table: 'cmdb_ci_server',
    defaultImage: 'unixservers.png',
    operationalStatus: '1'
};

function getServerTypes() {
    var serverTypes = [];
    var totalServers = 0;

    var aggregate = new GlideAggregate(CONFIG.table);
    aggregate.addAggregate('COUNT');
    aggregate.addQuery('operational_status', CONFIG.operationalStatus);
    aggregate.groupBy('sys_class_name');
    aggregate.query();

    while (aggregate.next()) {
        var sysClassName = aggregate.sys_class_name.toString();
        var count = parseInt(aggregate.getAggregate('COUNT'), 10);
        var info = SERVER_CLASS_MAPPING[sysClassName] || {
            name: sysClassName,
            image: CONFIG.defaultImage
        };

        serverTypes.push({
            name: info.name,
            image: info.image,
            count: count,
            construcao: info.construcao || false,
            sys_class_name: sysClassName
        });

        totalServers += count;
    }

    serverTypes.sort(function(a, b) {
        return b.count - a.count;
    });

    return {
        serverTypes: serverTypes,
        totalServers: totalServers,
        totalTypes: serverTypes.length
    };
}

try {
    if (!input || input.action === 'refreshServers') {
        var result = getServerTypes();
        data.serverTypes = result.serverTypes;
        data.totalServers = result.totalServers;
        data.totalTypes = result.totalTypes;
    }
} catch (error) {
    gs.error('Erro ao obter servidores: ' + error.message);
    data.serverTypes = [];
    data.totalServers = 0;
    data.totalTypes = 0;
}
