sp_widget.client_script:
api.controller = function($scope, $rootScope) {
    var c = this;

    var VIEW_CONFIG = {
        servers: {
            table: 'cmdb_ci_server',
            iframeUrl: '/sp?id=cmdb_servers_page'
        },
        network: {
            table: 'cmdb_ci_netgear',
            iframeUrl: '/sp?id=cmdb_network'
        }
    };

    c.currentView = 'servers';

    c.changeView = function(viewName) {
        if (c.currentView === viewName || !VIEW_CONFIG[viewName]) {
            return;
        }

        c.currentView = viewName;
        var config = VIEW_CONFIG[viewName];

        _saveTablePreference(config.table);
        $rootScope.$emit('cmdb.view.changed', {
            view: viewName,
            iframeUrl: config.iframeUrl
        });
    };

    function _saveTablePreference(tableName) {
        try {
            var ga = new GlideAjax('global.PortalFilterPrefs');
            ga.addParam('sysparm_name', 'saveTable');
            ga.addParam('sysparm_table', tableName);

            var timeout = setTimeout(function() {
                console.warn('GlideAjax timeout:', tableName);
            }, 5000);

            ga.getXMLAnswer(function(response) {
                clearTimeout(timeout);

                if (response === 'ok') {
                    console.log('Preferência salva:', tableName);
                } else {
                    console.error('Erro ao salvar preferência:', response);
                }
            });
        } catch (error) {
            console.error('GlideAjax error:', error.message);
        }
    }

    c.$onInit = function() {
				_saveTablePreference('cmdb_ci_server');
        c.changeView('servers');
    };
};

